import React,{useState,useEffect} from "react";
import TableTr from "./TableTr";
import CountingRows from "./countRows/CountingRows"
import Pagination from "react-js-pagination";

require("bootstrap-less/bootstrap/bootstrap.less");






const Position = ()=>{
    const products = [
        {trade_id: "1", client: 'Shoes',instrument: 'Running Shoes.',quantity:"asdasd",direction:"asdasd" },
        {trade_id: 1, client: 'Shoes',instrument: 'Running Shoes.',quantity:"asdasd",direction:"asdasd" },
    ];
    // const [data,setData] = useState(products)
    // const [row,setRows] = useState()
    // const [pageIsLoad,setPageIsLoad]= useState(0)   
    const [countPage,setCountPage]=useState(12)
    const [countRow,setCountRow]=useState(12)

    const [{data,pageIsLoad,setPageIsLoad,setData},getData,CountingRowsByData] = CountingRows("url"); 
    // const [countRow,setCountRow]=useState(2)
    // const limitCountPage =20;
    

    useEffect(()=>{
        fetch("/selectTr",{
            method:"GET",
            
        }).then(
            res=>res.json()
        ).then(data=>{
            
            
            let d = CountingRowsByData(data)
            // setRows(d)
            console.log(d)
            setPageIsLoad(true)
            setData(d)
            
            setCountRow(data.length)
            // console.log("countRow=>"+countRow)
        })
    },[])
    
    useEffect(()=>{
        // console.log(countRow)
        if(!pageIsLoad){
            return
        }
        // // console.log(row.lenght);
        // let size=data.length
        // setCountRow(size)
        // let pages =  countRow / limitCountPage
        console.log(countRow)
        // ii(pages)
    },[data])

   

    return (
    <div>
        <div className="table-div">
            <TableTr data={data} direction="true"/>
         
        </div>
        <div className="container">
            <ul className="pagination">
                <li><a href="#">1</a></li>
                <li className="active"><a href="#">2</a></li>
                <li><a href="#">3</a></li>
                <li><a href="#">4</a></li>
                <li><a href="#">5</a></li>
            </ul>
        </div>
    </div>
      
    )
}

export default Position
