import React,{useState,useEffect} from "react";
import TableTr from "./TableTr";
import Pagination from "react-js-pagination";
require("bootstrap-less/bootstrap/bootstrap.less");

const CountingRows = (data)=>{
    data.end.sort(function(a,b){
        return a.instrument.localeCompare(b.instrument)
    })
    data.end.sort(function(a,b){
        return a.client.localeCompare(b.client)
    })
    let rowss = [];
    // Check first direction
    if (data.end[0].direction==="B"){
        rowss.push(data.end[0])
    }
    else{
        data.end[0].quantity=0-parseInt(data.end[0].quantity)
        rowss.push(data.end[0])
    }

    data.end.shift()
    let j = 0;

    // Count quantity 
    data.end.forEach((element,index)=>{
        if(rowss[j].client === element.client && rowss[j].instrument === element.instrument){
            if (element.direction==="B"){
                rowss[j].quantity+=parseInt(element.quantity)
            }
            else{
                rowss[j].quantity-=parseInt(element.quantity)
            }
        }else{
            if (element.direction==="B"){
                rowss.push(element)
            }
            else{
                element.quantity=0-parseInt(element.quantity)
                rowss.push(element)
            }
            j++;

        }
    })
    return rowss;
    
}




const Position = ()=>{
    const products = [
        {trade_id: "1", client: 'Shoes',instrument: 'Running Shoes.',quantity:"asdasd",direction:"asdasd" },
        {trade_id: 1, client: 'Shoes',instrument: 'Running Shoes.',quantity:"asdasd",direction:"asdasd" },
    ];
    const [data,setData] = useState(products)
    const [row,setRows] = useState([])
    const [pageIsLoad,setPageIsLoad]= useState(0)
    const [totalCounRow,setTotalCountRow]=useState(0)
    const limitCountPage =20;

    useEffect(()=>{
        fetch("/selectTr",{
            method:"GET",
            
        }).then(
            res=>res.json()
        ).then(data=>{
            
            let d = CountingRows(data)
            setRows(d)
            setPageIsLoad(true)
            setData(d)
        })
    },[])
    
    useEffect(()=>{
        if(!pageIsLoad){
            return
        }
        console.log(row.lenght);
        setTotalCountRow(row.lenght)
        console.log(totalCounRow)
    },[row])

    return (
    <div>
        <div className="table-div">
            <TableTr data={data}/>
         
        </div>
        <div className="container">
            <ul className="pagination">
                <li><a href="#">1</a></li>
                <li className="active"><a href="#">2</a></li>
                <li><a href="#">3</a></li>
                <li><a href="#">4</a></li>
                <li><a href="#">5</a></li>
            </ul>
        </div>
    </div>
      
    )
}

export default Position
