import React,{useState} from "react";



const HomePage = ()=>{

    const [csvfile,setSCV] = useState();
    const [textFile,setTextFile] =useState("Click by me");
    const [ValidFile,setValidFile] =useState(0);

    function ver (e){
        if(e.target.files[0].type==="application/vnd.ms-excel"){
            console.log("Its scv file")
            setSCV(e.target.files[0])
            setTextFile("Successfully uploaded file")
            setValidFile(1)

        }else{
            console.log("Its not scv file")
            setTextFile("The file type does not correspond to reality")
            setValidFile(0)
        }
    }

    const SendRequest = ()=>{
        console.log("send requset ")
        fetch("/create_db",{
            method:"POST",
            body: {file:csvfile}
        }).then(
            res=>res.json()
        ).then(
            data=>console.log(data)
        )
    
    }
    // (e)=>{setSCV(e.target.files[0])}
    return (
        <div className="row" id="">    
            <div className="container">
                <div style={{textAlign:"center"}}><h1 style={{color:"black"}}>You need to uploud file</h1></div> 
                {/* <form type="file" method="POST"> */}
                <div className="uploud-file">
                    <input className="input-file" type="file" accept=".csv" id = "file" onChange={(e)=>{ver(e)}}></input>
                   {ValidFile?
                    <label for="file" className="success-text" >{textFile}</label>:
                    <label for="file" className="" >{textFile}</label>
                   }
                </div>
                    <button onClick={SendRequest}>Send file</button>
                    <FontAwesomeIcon icon="fa-solid fa-folder-arrow-up" />
                {/* </form> */}
        </div>
        </div>
    )

}

export default HomePage
