import React,{useState,useEffect} from "react";
import TableTr from "./TableTr";
import Pagination from "react-js-pagination";
require("bootstrap-less/bootstrap/bootstrap.less");

const CountingRows = (data)=>{
    
    let rows = [];
    // Check first direction
    if (data.end[0].direction==="B"){
        rows.push(data.end[0])
    }
    else{
        data.end[0].quantity=0-parseInt(data.end[0].quantity)
        rows.push(data.end[0])
    }

    data.end.shift()
    let j = 0;

    // Count quantity 
    data.end.forEach((element,index)=>{
        if(rows[j].client === element.client && rows[j].instrument === element.instrument){
            if (element.direction==="B"){
                rows[j].quantity+=parseInt(element.quantity)
            }
            else{
                rows[j].quantity-=parseInt(element.quantity)
            }
        }else{
            if (element.direction==="B"){
                rows.push(element)
            }
            else{
                element.quantity=0-parseInt(element.quantity)
                rows.push(element)
            }
            j++;

        }
    })
    return rows;
    
}




const Position = ()=>{
    const products = [
        {trade_id: "1", client: 'Shoes',instrument: 'Running Shoes.',quantity:"asdasd",direction:"asdasd" },
        {trade_id: 1, client: 'Shoes',instrument: 'Running Shoes.',quantity:"asdasd",direction:"asdasd" },
    ];
    const [data,setData] = useState(products)
    const [pageIsLoad,setPageIsLoad]= useState()
    const [totalCounRow,setTotalCountRow]=useState()
    const limitCountPage =20;
    let rows;

    useEffect(()=>{
        fetch("/selectTr",{
            method:"GET",
            
        }).then(
            res=>res.json()
        ).then(data=>{
            console.log(data)
            data.end.sort(function(a,b){
                return a.instrument.localeCompare(b.instrument)
            })
            data.end.sort(function(a,b){
                return a.client.localeCompare(b.client)
            })

         
            
            let rows = CountingRows(data)
            useEffect
            // setData(rows)
        })
    },[])
    
    
    return (
    <div>
        <div className="table-div">
            <TableTr data={data}/>
         
        </div>
        <div className="container">
            <ul className="pagination">
                <li><a href="#">1</a></li>
                <li className="active"><a href="#">2</a></li>
                <li><a href="#">3</a></li>
                <li><a href="#">4</a></li>
                <li><a href="#">5</a></li>
            </ul>
        </div>
    </div>
      
    )
}

export default Position
