import React,{onChange} from "react";



const HomePage = ()=>{

    onChange((e) => {
        console.log(e.target)
        let file = e.target.files;
        let reader = new FileReader();
        reader.readAsDataURL(file[0]);
        console.log(reader)
        reader.onload = (e) => {
          const url = "http://localhost:3000/data";
          const formData = { file: e.target.result };
          return postMessage(url, formData).then((response) =>
            console.warn("result!!", response)
          );
        };
      })

    return (
        <div class="row" id="">    
        <div style={{textAlign:"center"}}>Uploud file</div> 
        {/* <form type="file" method="POST"> */}
            <input type="file"></input>
            <button type="submit" onClick={(e) => this.onChange()}>Subbmit</button>

        {/* </form> */}
        </div>
    )
}

export default HomePage
